mapper.select:
SELECT menu_id AS id, menu.name, COUNT(menu_id) AS count, SUM(orders.price) AS price
FROM orders, menu
WHERE menu_id=#{id} AND menu.id=#{id}
GROUP BY menu_id;

SELECT store_id AS id, store.name, COUNT(store_id) AS count, SUM(total_orders.price) AS price
FROM total_orders, store
WHERE store_id=#{id} AND store.id=#{id}
GROUP BY store_id;
